CREATE TABLE yii2basic.activity
(
    id_activity int(11) PRIMARY KEY NOT NULL AUTO_INCREMENT,
    id_user int(11),
    title varchar(150) NOT NULL,
    date_start datetime NOT NULL,
    date_end datetime,
    is_repeatable tinyint(1) DEFAULT '0',
    is_blocking tinyint(1) DEFAULT '0',
    date_created timestamp DEFAULT CURRENT_TIMESTAMP NOT NULL,
    date_changed timestamp DEFAULT CURRENT_TIMESTAMP NOT NULL,
    description text,
    email varchar(150)
);
CREATE INDEX activity_userFK ON yii2basic.activity (id_user);
